mapper.update:
UPDATE board SET
update_time = unix_timestamp(),
title = #{title},
content = #{content}

filename= #{filename},

 WHERE brd_id = #{brd_id}

UPDATE board SET
viewcount = viewcount + 1
WHERE brd_id = #{brd_id}

UPDATE board SET
like = like + 1
WHERE brd_id = #{brd_id}

UPDATE board SET
dislike = dislike + 1
WHERE brd_id = #{brd_id}
