mapper.select:
select loc_id, state, city, dong, gu, r 
from ( select loc_id, state, city, dong, gu, rownum r
from ( select loc_id, state, city, dong, gu )
FROM local


	 and state = #{state}


	 and city = #{city}


	 and gu = #{gu}


ORDER BY state ASC, city ASC, gu ASC, dong ASC)
)where r>=#{sno} and r<=#{eno}

select count(*) from local


	 and state = #{state}


	 and city = #{city}


	 and gu = #{gu}
